insert into cfg_5_2.protocoldata (id, title, "Title", "Core", "Category", "Keyword", "Date__mm_dd_yyyy_", "Overview") values (39, E'Protocol : cfgPTC_242', E'Protocol Biotinylated Samples', E'H', E'Lectin-Glycan Binding', E'Biotinylated Samples', E'02/13/2009', E'Biotinylated sample is diluted to an appropriate concentration in standard binding buffer (TSM, 1% BSA and 0.05% Tween 20).  Seventy microliters is applied to the printed surface of the array, cover slip is applied, and incubated at room temperature in a humidified chamber away from light for one hour. The cover slip is removed and the slide is rinsed 4 times in standard TSM washing buffer containing 0.05% Tween and 4 times in TSM buffer without Tween.  Seventy microliters of fluorescently labeled streptavidin in standard TSM binding buffer is added to the slide for one hour at room temperature in a humidified chamber away from light as described.  Washes are performed as above, followed by 4 washes in distilled water. 
