insert into cfg_5_2.experimentdata (id, title, "Status", "Title", "-1100022276", "Glycan_Array_Version", "Protocol_Id", "Replicate_Number", "Result_Nature", "1505767802", "-1183338289", "-491792363", "Assay_Information", "Annotation", "1635439566", "1137989877", "-1069682899", "819836718") values (47, E'Primary Screen : primscreen_6330', E'Public', E'The purpose of this request is to analyze the H10N8 HA (ectodomain in trimeric form), which was expressed and purified from insect cells, on the glycan array.', E'cfg_rRequest_3029', E'PA_v52', E'Protocol Tertiary step', E'1', E'Data', E'200 micrograms/ml', E'06/04/2014', E'Sheng CUI', E'Sample was diluted and detected with anti-H10 antibody (polyclonal, sent by PI), followed by anti-rabbit IgG-488.', E'HA_H10N8', E'HA_H10N8', E'Download', E'Search Readout', E'Click This Link to Launch the New Flex-based Glycan Array Data Browser Tool');
insert into cfg_5_2.requestdata (id, title, "Request_ID", "Status", "Project_Description", "CFG_Member?", "Requester_First_Name", "Requester_Last_Name", "447911925", "1339104825", "Assigned_First_Name", "Assigned_Last_Name", "Requester_Email", "Request_Date", "Institution", "Shipping_Address", "Comments", "CFG_Core", "Amount_Requested", "-643239159", "2039874986", "5943919") values (47, E'cfg_rRequest_3029', E'cfg_rRequest_3029', E'Approved', E'The purpose of this request is to analyze the H10N8 HA (ectodomain in trimeric form), which was expressed and purified from insect cells, on the glycan array.', E'No', E'Sheng', E'Cui', E'Sheng', E'Cui', E'Sheng', E'CUI', E'cui.sheng@ipb.pumc.edu.cn', E'03/27/2014', E'Institute of Pathogen Biology at Peking Union Medical School', E'Building No.7&#65292; Bei Gong Da Ruan Jian Yuan, BDA, Beijing, 100176 China', E'we will ship the protein on ice to the provided address. It will take normally 2 weeks.', E'H', E'', E'A novel influenza strain H10N8 was recently isolated from a patient (died 6th DEC, 2013) in Jiangxi province, southern China. Our current data (unpublished) demonstrate that this avian influenza virus is capable of infecting human cells. Therefore, we plan to investigate the molecular basis underlying the cross-species infection by the new bird flu. Our project focuses on the interactions of human origin receptor analogs with H10N8 hemagglutinin, and avian origin receptor analogs with the hemagglutinin. We have expressed and purified the H10N8 HA ectodomain in trimeric form from insect cells. Our preliminary data from Biacore analysis shows that h10n8 HA binds both human and bird glycon receptor analogues. Therefore, we want to further investigate glycon receptors specificity of h10n8 HA using glycan array screening.', E'N', E'* 3. Identify the ligand(s) that mediate GBP binding');
