insert into cfg_5_2.sampledata (id, title, "Name", "Complete_Name", "Family", "Sub_Family", "Spec_Sci_Name", "Estimated_purity", "1001016772", "Status", "Comments", "-436221455") values (48, E'Glycan Binding Protein : cbp_3345', E'CnMBL', E'Clitocybe nebularis mucin binding lectin', E'Plant/Fungal Lectin', E'Fungal lectin', E'Clitocybe nebularis', E'Heterogenous sample with two major bands seen on SDS-PAGE', E'07/03/2014', E'Not Finalized', E'Lectin was submited to four step serial affinity chromatography (first on Sepharose-> unbound fraction was then applied to de-O-acetylmucin-Sepharose -> unbound fraction collected and applied two times on mucin-Sepharose). Better binding to mucin compared to de-O-acetylmucin and asialomucin was confirmed by lectin blot analysis.', E'Fungal fruiting bodies');
