insert into cfg_5_2.requestdata (id, title, "Request_ID", "Status", "Project_Description", "CFG_Member?", "Requester_First_Name", "Requester_Last_Name", "447911925", "1339104825", "Assigned_First_Name", "Assigned_Last_Name", "Requester_Email", "Requester_Interest", "Request_Date", "Institution", "Shipping_Address", "Comments", "CFG_Core", "Resource_Type", "Amount_Requested", "-643239159", "2039874986", "-1235327866", "GBP_being_Addressed", "5943919") values (68, E'cfg_rRequest_2921', E'cfg_rRequest_2921', E'Approved', E'This is a request for the glycan array analysis of a mannose binding lectin isolated from Edible tuber Dioscorea bulbifera.', E'Yes subgroups :3 :Glycans in cancer biology', E'Dr. Shashikala', E'Inamdar', E'Dr. Murugiswamy', E'Bale', E'Shashikala', E'Inamdar', E'pinkisri2001@yahoo.co.in', E'This laboratory focused in lectins from fungi & plants, studying their interaction with cell surface molecules for their possible application in cancer research.', E'08/30/2013', E'Karnatak University Dharwad, India', E'David F,Smith Professor of Biochemistry, Director of core H, Emory University, School of medicine, Dept. of Biochemistry, O. Wayne Rollins Research centre, Room No - 40201510 Clifton road, NE, Atlanta, GA - 30322', E'We request for the glycan array analysis of a mannose binding lectin isolated from Edible tuber Dioscorea bulbifera and purified by affinity chromatography. Lectin activity has been confirmed by hemagglutination assay using trypsinized rabbit erythrocytes and sugar specificity is confirmed by hapten inhibition studies. which reveals its specificity towards some complex glycans such as fetuin, asialofetuin, mucin, asialomucin and high mannose glycan moiety but not for simple sugars.', E'H', E'MicroArray', E'', E'Mannose binding lectins are known for their insecticidal and cell biological applications. However there are only two reports on lectins from Dioscoria species and no reports available on their fine sugar specificity. Hence it needs detailed glycan array analysis of this new mannose binding lectin to explore its possible applications.', E'Y', E'Dr. Shashikala R. Inamdar', E'Biotinylated lectin', E'* 1. Define the specificity and affinity for carbohydrate ligands');
